import "./App.css";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Home from "./components/Home";
import Title from "./components/Title";
import Game from "./components/Game";
import Axios from "axios";
import "./styles/Home.css";
import "./styles/Title.css";
import "./styles/Game.css";
import { useState } from "react";
import io from "socket.io-client";

function App() {
  const [displayGame, setDisplayGame] = useState(false);
  const [pName1, setPName1] = useState("");
  const [pName2, setPName2] = useState("");
  const [value, setValue] = useState("");
  const [oppName, setOppName] = useState("");
  const socket = io("http://localhost:3001");

  socket.on("find", (e) => {
    let name = "";
    name = document.getElementById("name").value;
    let allPlayersArray = e.allPlayersArray;
    console.log(allPlayersArray);
    let arrLen = allPlayersArray.length;
    const foundObj = allPlayersArray.find((obj) => {
      obj.p1.name == `${name}` || obj.p2.name == `${name}`;
    });
    if (foundObj) {
      foundObj.p1.name == `${name}`
        ? (oppName = foundObj.p2.name)
        : (oppName = foundObj.p1.name);
      foundObj.p1.name == `${name}`
        ? (value = foundObj.p1.value)
        : (value = foundObj.p2.value);
    }
    let name2 = allPlayersArray[arrLen - 1].p2.name;
    setPName1(name);
    setPName2(oppName);
    console.log("socket recieved by client");

    setDisplayGame(true);
  });

  return (
    <div className="App container-fluid">
      <div>
        <meta charSet="UTF-8" />
        <meta name="viewport" content="width=device-width, initial-scale=1.0" />
        <meta httpEquiv="X-UA-Compatible" content="ie=edge" />
        <title>Tic-Tac-Toe</title>
        <link rel="stylesheet" href="style.css" />
      </div>

      <Title />
      {displayGame ? <Game you={pName1} opponent={pName2} yourValue={value}/> : <Home />}
    </div>
  );
}

export default App;
